var Menu = {
  // These variables will be set on load
  is_front_page: false,
  page_root: null,
  header: null,
  chapters: null,
  chapter_items: null,
  chapter_offsets: null,

  menubarHeight: 0,
  chaptersStickyPoint: 0,
  stickyPoint: 0,
  inversePoint: 0,

  load: function() {
    console.log('loading Menu.js')
    Menu.page_root = $('html')
    Menu.header = $('header')
    Menu.chapters = $('.chapters')
    Menu.submenu = $('.menu .item .container')
    Menu.chapter_items = Menu.chapters.find('.item')
    Menu.header.css('height', 'auto')
    Menu.header.on('click', '.burger-button', Menu.toggle_menu)
    Menu.header.on('click', '.menu .button ', Menu.toggle_menu_item)
    Menu.menubarHeight = Menu.header.find('.menubar').outerHeight(true)

    Menu.page_root.removeClass('show-menu')

    $(window).scroll(Menu._on_scroll)
    $(window).resize(Menu._on_resize)
    Menu._on_resize()

    //zenscroll.setup(null, Menu.menubarHeight + Menu.chapters.outerHeight() + 10)
    Menu._on_scroll()
  },

  toggle_menu: function() {
    Menu.page_root.toggleClass('show-menu')
    Menu.submenu.each(function () {
        $(this).closest('.item').addClass('item-list')
    })
  },

  toggle_menu_item: function(e) {
    if (Menu.page_root.hasClass('show-menu') && $(this).closest('.item').hasClass('item-list')){
        e.preventDefault()
        $(this).closest('.item').toggleClass('show-content')
    }
  },

  _on_resize: function() {
    Menu.stickyPoint = Menu.header.outerHeight(true) - Menu.menubarHeight

    $banner = $('section.format-banner:first-child')
    if ($banner.length > 0 && $banner.children('.content').hasClass('inverse')) {
      if ($banner.children('.content').hasClass('style-contact-page')) {
        Menu.inversePoint = Menu.stickyPoint
      } else {
        Menu.inversePoint = $banner.outerHeight() - Menu.menubarHeight
      }
    } else {
      Menu.inversePoint = 0
    }

    if (Menu.chapters.length > 0) {
      Menu.chaptersStickyPoint = Menu.chapters.offset().top
      let offset_adjustment = Menu.stickyPoint + 120

      Menu.chapter_offsets = []
      Menu.chapter_items.each(function() {
        let id = $(this).attr('href')
        let $target = $(id)
        Menu.chapter_offsets.push($target.offset().top - offset_adjustment)
      })

      $last_section = $('article > section:last-child')
      Menu.chapter_offsets.push($last_section.offset().top + $last_section.outerHeight(true) - offset_adjustment)
    }
  },

  _on_scroll: function() {
    let scrollTop = $(window).scrollTop()
    let $headerContainer = Menu.header.find('.container:visible').first()
    let headerHeight = $headerContainer.hasClass('desktop') ? $headerContainer.find('.menubar').outerHeight() : $headerContainer.outerHeight()

    if (scrollTop > Menu.stickyPoint - headerHeight + 2) {
      if (!Menu.header.hasClass('sticky') && $headerContainer.hasClass('desktop')) {
        Menu.header.css('height', Menu.header.outerHeight() + 'px')
        Menu.header.addClass('sticky')
      }
    } else {
      Menu.header.css('height', 'auto')
      Menu.header.removeClass('sticky')
    }

    if (Menu.inversePoint > 0) {
      Menu.header.toggleClass('inverse', scrollTop < Menu.inversePoint)
    }

    if (Menu.chaptersStickyPoint > 0) {
      let $chaptersContainer = Menu.chapters.find('.container')
      let chaptersHeight = $chaptersContainer.outerHeight()

      console.log(scrollTop, '>', Menu.chaptersStickyPoint, '-', headerHeight, '=', scrollTop > Menu.chaptersStickyPoint - headerHeight)

      if (scrollTop < Menu.chaptersStickyPoint - headerHeight) {
        Menu.chapters.css('height', 'auto')
        $chaptersContainer.css('top', 'auto')
        Menu.chapters.removeClass('sticky')
      } else if (!Menu.chapters.hasClass('sticky')) {
        Menu.chapters.css('height', chaptersHeight + 'px')
        $chaptersContainer.css('top', headerHeight + 'px')
        Menu.chapters.addClass('sticky')
      }
    }

    Menu.chapter_items.each(function(index) {
      let $item = $(this)
      let targetTop = Menu.chapter_offsets[index]
      let targetBottom = Menu.chapter_offsets[index+1]
      let $progress = $item.children('.progress')

      if (scrollTop < targetBottom) {
        let percentage = (Math.max(0, Math.min(1, (scrollTop - targetTop) / (targetBottom - targetTop)))) * 100
        $progress.css('width', percentage + '%')
      } else {
        $progress.css('width', '100%')
      }

      $item.toggleClass('active', (targetTop < scrollTop && scrollTop < targetBottom))
    })
  },
}

$(document).on('turbolinks:load', function() { Menu.load() })
;

var Music = {
  // These variables will be set on load
  player: null,
  player_title: null,
  player_artist: null,
  mood_title: null,

  list: null,
  track_selectors: null,

  cover: null,
  default_cover_url: null,
  filter_icons: null,

  instrument_filters: null,
  mood_filters: null,

  load: function() {
    console.log('loading Music.js');
    var player = $('audio#player');
    var controls = player.find('data-controls').text();
    Music.player = new Plyr(player, {
      controls,
      invertTime: false
    });

    Music.player_title = $('#track-player-title')
    Music.player_artist = $('#track-player-artist')
    Music.mood_title = $('#track-mood-title')
    Music.instrument_filters = $('.instrument.filters > a.filter')
    Music.mood_filters = $('.mood.filters > a.filter')

    Music.list = $('.playlist #grid')
    Music.track_selectors = Music.list.find('.track > a.info')
    Music.track_selectors.each(Music._init_track)
    Music.track_selectors.on('click', Music._on_select_track)

    Music.cover = $('#track-cover')
    Music.default_cover_url = Music.cover.attr('href')

    Music.filter_icons = $('#selected-filter-icons')

    Music.instrument_filters.on('mouseup', Music._on_clicked_instrument_filter)
    Music.mood_filters.on('mouseup', Music._on_clicked_mood_filter)
  },

  _init_track: function() {
    var info = $(this)
    var audio = new Audio()

    $(audio).on('loadedmetadata', function() {
      info.siblings('.duration').text(Music.format_duration(audio.duration))
    })

    audio.src = this.href
  },

  _on_clicked_instrument_filter: function() {
    var element = $(this)
    var klass = 'for-'+this.dataset.filter.slice(1)

    if (element.hasClass('active')) {
      Music.filter_icons.children('.'+klass).remove()
    } else {
      var icon = $(element.html()).addClass(klass)
      Music.filter_icons.append(icon)
    }
  },

  _on_clicked_mood_filter: function() {
    if (Music.mood_title.text() == this.innerText) {
      Music.mood_title.text('')
    } else {
      Music.mood_title.text(this.innerText)
    }
  },

  _on_select_track: function(e) {
    var image_url = this.dataset.artistImage
    Music.player_title.text(this.textContent)
    Music.player_artist.text(this.dataset.artistName)
    Music.player_artist.attr('href', this.dataset.artistUrl)

    if (!image_url) {
      image_url = Music.default_cover_url
    }

    Music.cover.attr('src', image_url)
    Music.player.source = {
      type: 'audio',
      title: this.textContent,
      sources: [
        { src: this.href, type: 'audio/mp3' },
      ]
    }

    Music.player.play()

    Music.list.find('.track').removeClass('active')
    $(this).closest('.track').addClass('active')
    e.preventDefault()
  },

  format_duration: function(seconds) {
    var sec_num = parseInt(seconds, 10) // don't forget the second param
    var hours   = Math.floor(sec_num / 3600)
    var minutes = Math.floor((sec_num - (hours * 3600)) / 60)
    var seconds = sec_num - (hours * 3600) - (minutes * 60)

    if (hours   < 10) {hours   = "0"+hours}
    if (minutes < 10) {minutes = "0"+minutes}
    if (seconds < 10) {seconds = "0"+seconds}

    if (hours > 0)
      return hours+':'+minutes+':'+seconds
    else
    return minutes+':'+seconds
  },
}

$(document).on('turbolinks:load', function() { Music.load() })
;

